INSERT INTO help VALUES (70, 'A', '<strong>AJAX :</strong> (Asynchronous Javascript And Xml)', 68);
INSERT INTO help VALUES (71, 'B', '<p><strong><span style="font-size: 10px" /></strong><strong>Base de donn&eacute;es</strong> : Fichier ou ensemble de fichiers disque ou m&eacute;moire permettant le stockage permanent ou temporaire et l''acc&egrave;s &agrave; des informations structur&eacute;es.<br /><br /><strong>Blog</strong> : Carnets, journaux personnels en ligne qui permettent &agrave; un internaute de publier au jour le jour ses p&eacute;r&eacute;grinations sur le Net ou de commenter l''actualit&eacute; d''un sujet qui l''int&eacute;resse.<br /></p> ', 68);
INSERT INTO help VALUES (72, 'C', '<p><strong><span style="font-size: 10px" /></strong><strong>CSM</strong> <em>: (Content Managing System ou en Fran&ccedil;ais Syst&egrave;me de gestion de contenus)</em> Logiciel de conception et de mise &agrave; jour dynamique de sites.<br /></p> ', 68);
INSERT INTO help VALUES (73, 'F', '<p><strong><span style="font-size: 10px" /></strong><strong>FAQ</strong> : Rubrique pr&eacute;sentant par sujets les questions les plus fr&eacute;quemment pos&eacute;es par les utilisateurs, accompagn&eacute;es des r&eacute;ponses correspondantes.<br /><br /><strong>FireFox</strong> : Navigateur internet d&eacute;riv&eacute; de Mozilla qui est la version open source de Netscape. Firefox est compos&eacute; de nombreuses fonctionnalit&eacute;s comme la navigation par onglet ou le gestionnaire de t&eacute;l&eacute;chargements qui ne sont pas disponibles par d&eacute;faut sur les autres navigateurs.<br /><br /><strong>Forum</strong> : Service permettant discussions et &eacute;changes sur un th&egrave;me donn&eacute; : chaque utilisateur peut lire &agrave; tout moment les interventions de tous les autres et apporter sa propre contribution sous forme d''articles.<br /><br /><strong>Funkylab</strong> : Syst&egrave;me de gestion de contenus.<br /></p> ', 68);
INSERT INTO help VALUES (74, 'G', '<p><strong><span style="font-size: 10px" /></strong><strong>GIF</strong> : <em>(Graphics Interchange Format)</em> Format de fichiers d''image le plus r&eacute;pandu sur le Web et invent&eacute; par Compuserve. Les fichiers au format GIF sont fortement compress&eacute;s (m&eacute;thode LWZ) tout en gardant une qualit&eacute; tr&egrave;s correcte. Le nombre maximal de couleurs contenu dans une image au format GIF est de 256.<br /></p> ', 68);
INSERT INTO help VALUES (75, 'J', '<p><strong><span style="font-size: 10px" /></strong><strong>JPEG</strong> : <em>(Join Photographic Experts Group)</em> Un standard pour la compression des images qui contiennent plusieurs millions de couleurs. Tr&egrave;s courant sur l''Internet.<br /></p> ', 68);
INSERT INTO help VALUES (76, 'L', '<p><strong><span style="font-size: 10px" /></strong><strong>Login</strong> : La proc&eacute;dure d''identification des utilisateurs sur un serveur.<br /></p> ', 68);
INSERT INTO help VALUES (77, 'P', '<p><strong><span style="font-size: 10px" /></strong><strong>PNG</strong> : <em>(Portable Network Graphic)</em> Format d''image libre de droits.<br /></p> ', 68);
INSERT INTO help VALUES (78, 'T', '<p><strong><span style="font-size: 10px" /></strong><strong>TAGS</strong> : Codes sp&eacute;cifiques utilis&eacute;s pour mettre en forme des documents html.<br /><br /><strong>Template</strong> : <br /></p> ', 68);
INSERT INTO help VALUES (79, 'W', '<p><strong><span style="font-size: 10px" /></strong><strong>Watermark </strong>: Algorithmes permettant de marquer de fa&ccedil;on visible ou invisible tout fichier contenant des informations soumises au copyright.<strong><br /><br />Workflow</strong> : Cha&icirc;ne de publication offrant la possibilit&eacute; de publier des documents.<br /></p> ', 68);
