CREATE TABLE IF NOT EXISTS admin (id int(11) NOT NULL auto_increment,autorisation varchar(255) NOT NULL default '',login varchar(255) NOT NULL default '',pass varchar(255) NOT NULL default '',nom varchar(255) NOT NULL default '',prenom varchar(255) NOT NULL default '',email varchar(255) NOT NULL default '',datecreation datetime NOT NULL default '0000-00-00 00:00:00',derniereconnexion datetime NOT NULL default '0000-00-00 00:00:00',admin varchar(255) NOT NULL default '',optionadmin varchar(255) NOT NULL default '',superadmin int(11) NOT NULL default '0',skinadmin varchar(255) NOT NULL default '',compteweb int(11) NOT NULL default '0',PRIMARY KEY id (id)) ENGINE=MyISAM;
CREATE TABLE IF NOT EXISTS bannieres (id int(11) NOT NULL auto_increment,url char(255) NOT NULL default '',image char(255) NOT NULL default '',nbclic int(11) NOT NULL default '0',PRIMARY KEY id (`id`)) ENGINE=MyISAM;
CREATE TABLE IF NOT EXISTS billet (id int(11) NOT NULL auto_increment,ordre int(11) default '0',nom varchar(255) NOT NULL default '',visible int(11) default '0',parent int(11) NOT NULL default '0',overrideskin varchar(255) default NULL,type varchar(255) NOT NULL default '',option_element varchar(255) NOT NULL default '',zone varchar(255) default NULL,date_debut date NOT NULL default '0000-00-00',date_fin date NOT NULL default '0000-00-00',texte text,auteur int(11) NOT NULL default '0',premierepage int(11) NOT NULL default '0',PRIMARY KEY id (id)) ENGINE=MyISAM;
CREATE TABLE IF NOT EXISTS blacklist (id int(11) NOT NULL auto_increment,mot char(255) NOT NULL default '',utilisateur int(11) NOT NULL default '0',PRIMARY KEY id (id)) ENGINE=MyISAM;
CREATE TABLE IF NOT EXISTS comments (id int(11) NOT NULL auto_increment,commentdate datetime NOT NULL default '0000-00-00 00:00:00',billetid int(11) NOT NULL default '0',pseudo varchar(255) NOT NULL default '',email varchar(255) NOT NULL default '',text text NOT NULL,ip_utilisateur varchar(255) NOT NULL default '',PRIMARY KEY id (id)) ENGINE=MyISAM;
CREATE TABLE IF NOT EXISTS config (nomsite varchar(255) NOT NULL default '',  skinsite varchar(255) NOT NULL default '',  skinadmin varchar(255) NOT NULL default '',  online varchar(255) NOT NULL default '',  watermark int(11) NOT NULL default '0',  watermark_image varchar(255) NOT NULL default '',  watermark_position varchar(255) NOT NULL default '',  filtrecom int(11) NOT NULL default '0',  id int(1) NOT NULL default '0') ENGINE=MyISAM; 
CREATE TABLE IF NOT EXISTS mail_newsletter (id int(11) NOT NULL auto_increment,  mail char(255) NOT NULL default '',  date datetime NOT NULL default '0000-00-00 00:00:00',  IPcreation char(255) NOT NULL default '',  KEY id (id)) ENGINE=MyISAM;
CREATE TABLE IF NOT EXISTS menu (id int(11) NOT NULL auto_increment,  ordre varchar(255) NOT NULL default '',  nom varchar(255) NOT NULL default '',  image varchar(255) NOT NULL default '',  type varchar(255) NOT NULL default '',  url varchar(255) NOT NULL default '',  parent int(11) NOT NULL default '0',  KEY id (id)) ENGINE=MyISAM; 
CREATE TABLE IF NOT EXISTS messagerie (id int(11) NOT NULL auto_increment,  fromqui varchar(255) NOT NULL default '',  recepteur varchar(255) NOT NULL default '',  date_envois datetime default '0000-00-00 00:00:00',  objet varchar(255) NOT NULL default '',  message text NOT NULL,  lu varchar(255) NOT NULL default '',  KEY id (id)) ENGINE=MyISAM;
CREATE TABLE IF NOT EXISTS newsletter (id int(11) NOT NULL auto_increment,  date datetime NOT NULL default '0000-00-00 00:00:00',  parqui varchar(255) NOT NULL default '',  send int(11) NOT NULL default '0',  senddate datetime NOT NULL default '0000-00-00 00:00:00',  titre varchar(255) NOT NULL default '',  edito text NOT NULL,  billets varchar(255) NOT NULL default '',  lastbillet int(11) NOT NULL default '0',  KEY id (id)) ENGINE=MyISAM;
CREATE TABLE IF NOT EXISTS photo (id int(50) NOT NULL auto_increment,  nom varchar(100) NOT NULL default '',  file varchar(100) NOT NULL default '',  description text NOT NULL,  type varchar(100) NOT NULL default '',  prix varchar(11) NOT NULL default '0',  tailleX varchar(100) NOT NULL default '',  tailleY varchar(100) NOT NULL default '',  motscle text NOT NULL,  date varchar(255) NOT NULL default '',  pays varchar(255) NOT NULL default '',  resolution varchar(255) NOT NULL default '',  plimus varchar(255) NOT NULL default '',  KEY id (id)) ENGINE=MyISAM;
CREATE TABLE IF NOT EXISTS type_photo (id int(11) NOT NULL auto_increment,  type varchar(255) NOT NULL default '',  parent int(11) NOT NULL default '0',  commentaire text NOT NULL,  KEY id (id)) ENGINE=MyISAM;
CREATE TABLE help (id int(11) NOT NULL auto_increment,titre varchar(255) NOT NULL default '',page text NOT NULL,parent int(11) NOT NULL default '0',PRIMARY KEY id (id)) ENGINE=MyISAM; 




